    public function import($module)
    {

        $method = $module . "DataImport";

        if (method_exists($this, $method)) {
            $this->$method();
        } else {
            $notification = array(
                'message' => "Method Not Exist",
                'alert-type' => 'error'
            );
            return redirect()->back()->with($notification);
        }

        //notification
        $notification = array(
            'message' => ucfirst($module) . ' Data Import Successfully ',
            'alert-type' => 'success'
        );
        return redirect()->back()->with($notification);
    }

