    public function countCategory()
    {
        $classes = InsClass::all();
        $catCount = 0;
        foreach ($classes as $class) {
            if ($class->categories) {
                $catCount += $class->categories->count();
            }
        }
        return $catCount;
    }

    public function countGroup()
    {
        $classes = InsClass::all();
        $catCount = 0;
        foreach ($classes as $class) {
            if ($class->groups) {
                $catCount += $class->groups->count();
            }
        }
        return $catCount;
    }
